@-moz-document regexp("https:\/\/(gist\.)*github\.com(?!\/(home|marketplace|organizations\/plan)).*") {
  [data-color-mode="light"],
  [data-color-mode="auto"] > .theme-light {
    #catppuccin(@lightFlavor, @accentColor);
  }
  @media (prefers-color-scheme: light) {
    [data-color-mode="auto"] {
      #catppuccin(@lightFlavor, @accentColor);
    }
  }
  [data-color-mode="dark"],
  [data-color-mode="auto"] > .theme-dark {
    #catppuccin(@darkFlavor, @accentColor);
  }
  @media (prefers-color-scheme: dark) {
    [data-color-mode="auto"] {
      #catppuccin(@darkFlavor, @accentColor);
    }
  }

  html:not([data-light-theme="light"]) body:not(.logged-out)::after,
  html:not([data-dark-theme="dark"]) body:not(.logged-out)::after {
    position: fixed;
    top: 0;
    left: 0;
    padding: 1rem;
    margin: 1rem;
    font-size: 0.9rem;
    font-weight: 600;
    border-radius: 0.5rem;
    max-width: 40ch;
    background-color: var(--color-danger-fg);
    color: var(--color-header-bg);
    content: "Unsupported GitHub theme detected! Please switch to the default light/dark theme via the GitHub Appearance settings to get the best experience for the Catppuccin GitHub userstyle.";
    z-index: 9999;
  }

  #coloredButton(@name, @color) {
    --color-btn-@{name}-text: @base;
    --color-btn-@{name}-bg: @color;
    --color-btn-@{name}-border: transparent;
    --color-btn-@{name}-shadow: 0 0 transparent;
    --color-btn-@{name}-inset-shadow: 0 0 transparent;
    --color-btn-@{name}-hover-bg: mix(@color, @text, 30%);
    --color-btn-@{name}-hover-border: rgba(240, 246, 252, 0.1);
    --color-btn-@{name}-selected-bg: mix(@color, @text, 50%);
    --color-btn-@{name}-selected-shadow: 0 0 transparent;
    --color-btn-@{name}-disabled-text: @crust;
    --color-btn-@{name}-disabled-bg: mix(@color, @base, 60%);
    --color-btn-@{name}-disabled-border: transparent;
    --color-btn-@{name}-focus-bg: #238636;
    --color-btn-@{name}-focus-border: rgba(240, 246, 252, 0.1);
    --color-btn-@{name}-focus-shadow: 0 0 0 3px rgba(46, 164, 79, 0.4);
    --color-btn-@{name}-icon: @base;
    --color-btn-@{name}-counter-bg: rgba(255, 255, 255, 0.2);
  }

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    &,
    & body {
      accent-color: @accent-color;
      --bgColor-default: @base;
      --color-canvas-default-transparent: rgba(13, 17, 23, 0);
      --color-page-header-bg: @base;
      --color-marketing-icon-primary: @sky;
      --color-marketing-icon-secondary: @blue;
      --color-diff-blob-addition-num-text: @text;
      --color-diff-blob-addition-fg: @text;
      --color-diff-blob-addition-num-bg: fadeout(@green, 70%);
      --color-diff-blob-addition-line-bg: fadeout(@green, 85%);
      --color-diff-blob-addition-word-bg: fadeout(@green, 70%);
      --color-diff-blob-deletion-num-text: @text;
      --color-diff-blob-deletion-fg: @text;
      --color-diff-blob-deletion-num-bg: fadeout(@red, 70%);
